mapper.insert:
insert into movie_detail (
	movieId, movieName, director, scriptwriter, actors, movieType,
	movieArea, movieLang, movieDate, movieSize, movieAlias, movieIMDb, movieDesc, rate, rateCount, ratePer
)
values (
	#{movieId}, #{movieName}, #{director}, #{scriptwriter}, #{actors}, #{movieType},
	#{movieArea}, #{movieLang}, #{movieDate}, #{movieSize}, #{movieAlias}, #{movieIMDb}, #{movieDesc}, #{rate}, #{rateCount}, #{ratePer}
)
